package com.hunter.study.algorithm.class03;import org.junit.Test;/** * @Author 水木 * @Date 2021/2/23  6:01 下午 */public class ReverseDoubleNodeList {	/**	 * 逆转双链表	 *	 * @param head	 */	public DoubleNode reverseDoubleNode(DoubleNode head) {		DoubleNode pre = null;		DoubleNode next = null;		while (head != null) {			next = head.next;			head.next = pre;			head.pre = next;			pre = head;			head = next;		}		return pre;	}	@Test	public void reverseDoubleNodeTest() {		int max = 100;		for (int i = 0; i < max; i++) {			final DoubleNode head = NodeUtils.generateRandomDoubleList(10, 10);			System.out.println("==========测试" + i + "=========");			System.out.println(NodeUtils.nodeToString(head));			System.out.println("修改后");			System.out.println(NodeUtils.nodeToString(reverseDoubleNode(head)));		}	}}