package com.hunter.study.designpattern.observer.jdk_v2;import java.util.Observable;import java.util.Observer;/** * @Author 水木 * @Date 2019-07-22  11:41 */public class Thief implements Observer {    private String name;    public Thief(String name) {        this.name = name;    }    @Override    public void update(Observable o, Object arg) {        System.out.println(Thread.currentThread() + "" + this.name + " 跑了");    }}