package com.hunter.study.designpattern.observer.jdk_v2;import java.util.Observable;/** * @Author 水木 * @Date 2019-07-22  11:50 */public class Watchman extends Observable {    @Override    public void notifyObservers(Object arg) {        System.out.println("警察来了。。。。。。快跑啊");        super.notifyObservers(arg);    }    @Override    public void setChanged(){        super.setChanged();    }}