package com.hunter.study.designpattern.singleton;/** * @Author zhangcl * @Date 18/5/7  下午11:58 * 线程安全 */public class SynSingleton {    private static SynSingleton singleton;    private SynSingleton() {    }    public static synchronized SynSingleton getInstance() {        if (singleton == null) {            singleton = new SynSingleton();        }        return singleton;    }}