package com.hunter.study.javabase.datatype;import java.text.DecimalFormat;/** * @Author zhangcl * @Date 17/3/13  上午10:13 */public class DataUtil {    private DataUtil() {    }    /**     * 保留小数点后n位     *     * @param number     * @param n     * @return     */    public static String reservePoint(double number, int n) {        if (n < 0 || n > 5) {            throw new IllegalArgumentException("param n must great than -1 and less than 6");        }        StringBuilder sb = new StringBuilder("#");        for (int i = 1; i <= n; i++) {            if (i == 1) {                sb.append(".");            }            sb.append("0");        }        DecimalFormat df = new DecimalFormat(sb.toString());        return df.format(number);    }}