package com.hunter.study.javabase.fatherandsub.fatherandsubinitialization;/** * @Author zhangcl * @Date 17/2/5  上午9:46 */public class FatherClass {    public String beforeVar = beforeMethod();    {        System.out.println("父类---代码块");    }    public String afterVar = afterMethod();    static {        System.out.println("父类---静态代码块");    }    public static String staticVar = staticMethod();    public FatherClass() {        System.out.println("父类---构造器");    }    public String beforeMethod() {        System.out.println("父类---初始化属性1");        return "";    }    public String afterMethod() {        System.out.println("父类---初始化属性2");        return "";    }    public static String staticMethod() {        System.out.println("父类---初始化静态属性");        return "";    }}