package com.hunter.study.javabase.fatherandsub.fatherandsubinitialization;/** * @Author zhangcl * @Date 17/2/5  上午9:53 */public class SubClass extends FatherClass{    public String beforeVar = beforeMethodSub();    {        System.out.println("子类---代码块");    }    public String afterVar = afterMethodSub();    static {        System.out.println("子类---静态代码块");    }    public static String staticVar = staticMethodSub();    public SubClass() {        System.out.println("子类---构造器");    }    public String beforeMethodSub() {        System.out.println("子类---初始化属性1");        return "";    }    public String afterMethodSub() {        System.out.println("子类---初始化属性2");        return "";    }    public static String staticMethodSub() {        System.out.println("子类---初始化静态属性");        return "";    }    public static void main(String[] args){        SubClass subClass=new SubClass();    }}