package com.hunter.study.javabase.list;import com.google.common.collect.Lists;import java.util.List;/** * @Author 水木 * @Date 2021/3/9  5:36 下午 */public class Test {	public static void main(String[] args) {		Body body=Body.builder()				.name("body1")				.build();		Body body2=Body.builder()				.name("body1")				.build();		List<Body> bodies=Lists.newArrayList();		bodies.add(body);		bodies.add(body2);		Person person =new Person();		person.setAge(1);		person.setName("1");		person.setBodyList(bodies);		System.out.println(person.toString());	}}