package com.hunter.study.leetcode;/** * @Author 水木 * @Date 2021/2/28  10:44 下午 */public class RemoveDuplicates {	public static void main(String[] args) {		int[] nums = {1, 2, 3} ;		System.out.println(removeDuplicates(nums));	}	public static int removeDuplicates(int[] nums) {		if (nums.length == 0) {			return 0;		}		int index = 0;		for (int i = 0; i < nums.length; i++) {			if (nums[i] == nums[index]) {				continue;			}			if (nums[i] > nums[index]) {				if (i - index == 1) {					index++;				} else {					nums[++index] = nums[i];				}			}		}		return index + 1;	}}