package com.hunter.study.redis.common;import redis.clients.jedis.Jedis;/** * @Author zhangcl * @Date 18/8/17  下午5:14 */public class RedisUtil {    public static Jedis jedis=new Jedis("127.0.0.1",6379);    public static String getData(String key){        return jedis.get(key);    }    public static String setData(String key,String value){        return jedis.set(key,value);    }    public static Long deleteData(String key){        return jedis.del(key);    }}