package com.hunter.study.thread;/** * @Author zhangcl * @Date 17/2/4  下午3:50 */public class RunnableDemo implements Runnable {    @Override    public void run() {        System.out.println(Thread.currentThread().getName() + "---我来了");    }    public static void main(String[] args) {        System.out.println(Thread.currentThread().getName() + "-------主线程来了");        RunnableDemo runnableDemo = new RunnableDemo();        for (int i = 0; i < 3; i++) {            Thread t = new Thread(runnableDemo);            t.start();        }    }}