package com.hunter.study.thread.copyOnWriteArrayList;import java.util.concurrent.CopyOnWriteArrayList;/** * @Author zhangcl * @Date 17/3/6  上午8:52 */public class MainClass {    public static void main(String[] args) {        final CopyOnWriteArrayList list = new CopyOnWriteArrayList();        new Thread(new Runnable() {            @Override            public void run() {                for (int i = 0; i < 100; i++) {                    list.add("main_" + i);                    System.out.println(Thread.currentThread().getName()+" add main "+i);                }            }        }).start();        try {            Thread.sleep(1);        } catch (InterruptedException e) {            e.printStackTrace();        }        for (int i = 0; i < list.size(); i++) {            System.out.println(Thread.currentThread().getName()+ "------"+ list.size());            System.out.println(Thread.currentThread().getName()+" get "+ list.get(i));        }    }}