package com.hunter.study.thread.holder;/** * @Author zhangcl * @Date 17/2/21  上午8:15 */public class Holder {    private int n;    public Holder(int n) {        System.out.println(Thread.currentThread().getName()+"执行构造函数");        this.n = n;    }    public void assertSanity() {        if (n != n) {            throw new AssertionError("this statement is false.");        }    }}