package com.hunter.study.thread.holder;/** * @Author zhangcl * @Date 17/2/21  上午8:24 */public class MainDemo {    public static void main(String[] args) {        Haver haver = new Haver();        for (int i = 0; i < 20; i++) {            Thread thread = new Thread(haver);            thread.start();        }        try{            Thread.sleep(3L);            haver.holder.assertSanity();        }catch (Exception e){            e.printStackTrace();        }    }}