package com.hunter.study.thread.simpledemo;/** * @Author zhangcl * @Date 17/2/4  下午3:41 */public class ThreadDemo extends Thread {    @Override    public void run() {        System.out.println(Thread.currentThread().getName() + "---我来了");    }    public static void main(String[] args) {        System.out.println(Thread.currentThread().getName() + "-------主线程来了");        for (int i = 0; i < 3; i++) {            ThreadDemo t = new ThreadDemo();            t.start();        }    }}