package com.hunter.study.thread.sychronized;/** * @Author zhangcl * @Date 17/2/7  下午11:03 */public class SynchronizedDemo {    public static void main(String[] args) {        final Demo demo = new Demo();        new Thread() {            @Override            public void run() {                demo.insert();            }        }.start();        new Thread() {            @Override            public void run() {                demo.insert1();            }        }.start();    }}class Demo {    private Object object=new Object();    synchronized void insert() {        for (int i = 0; i < 5; i++) {            try{                Thread.sleep(1000L);                System.out.println(Thread.currentThread().getName() + " insert " + i);            }catch (Exception e){                e.printStackTrace();            }        }    }     void insert1() {         synchronized (object){             for (int i = 0; i < 5; i++) {                 try{                     System.out.println(Thread.currentThread().getName() + " insert1 " + i);                 }catch (Exception e){                     e.printStackTrace();                 }             }         }    }}