package com.hunter.study.thread.threadlocal;/** * @Author zhangcl * @Date 17/10/26  下午10:33 */public class UserManager {    private static ThreadLocal<UserDemo> threadLocal = new ThreadLocal<>();    private static ThreadLocal<UserDemo> threadLocal2 = new ThreadLocal<>();    public void setName(String name) {        threadLocal.get().setName(name);    }    public static String getName() {        return threadLocal.get().getName();    }    public static void set(UserDemo userDemo) {        threadLocal.set(userDemo);    }    public static UserDemo get2() {        return threadLocal2.get();    }    public static String getName2() {        return threadLocal2.get().getName();    }    public static void set2(UserDemo userDemo) {        threadLocal2.set(userDemo);    }    public static UserDemo get() {        return threadLocal.get();    }}