package com.hunter.study.thread.threadlocal;/** * @Author zhangcl * @Date 17/10/26  下午10:33 */public class UserManager {    private static ThreadLocal<UserDemo> threadLocal = new ThreadLocal<>();    public void setName(String name) {        threadLocal.get().setName(name);    }    public void setAge(int i) {        threadLocal.get().setAge(i);    }    public String getName() {        return threadLocal.get().getName();    }    public void set(UserDemo userDemo) {        threadLocal.set(userDemo);    }    public UserDemo get() {        return threadLocal.get();    }}